import { Canvg } from 'canvg';

const A4Height300DPI = 3508;

interface TransformSVGOptions {
  svgString: string;
  width: number;
  height: number;
  maxHeight?: number;
  format?: string;
}

function transformSVGStringToImage(options: TransformSVGOptions): Promise<string[]> {
  const { svgString, width, height, maxHeight = A4Height300DPI, format = 'png' } = options;
  const canvas = document.createElement('canvas');
  const context = canvas.getContext('2d');
  canvas.width = width;
  canvas.height = height;
  const v = Canvg.fromString(context, svgString, {
    ignoreDimensions: true,
    scaleWidth: width,
    scaleHeight: height
  });
  const imageFormat = format || 'png';

  return v.render().then(() => {
    if (height > maxHeight) {
      return splitCanvas(canvas, width, height, maxHeight).map((canvasSegment) =>
        canvasSegment.toDataURL('image/' + imageFormat)
      );
    } else return [canvas.toDataURL('image/' + imageFormat)];
  });
}

function addLatoFontToSVG(svg: string): string {
  return svg.replace(
    /<\/svg/,
    '<defs><style>' +
      '@font-face {\n' +
      "    font-family: 'Lato';\n" +
      "    src: url(data:application/x-font-woff;charset=utf-8;base64,) format('woff');\n" +
      '    font-weight: 700;\n' +
      '    font-style: normal;\n' +
      '    -webkit-font-smoothing: antialiased;' +
      '}' +
      '@font-face {\n' +
      "    font-family: 'Lato';\n" +
      "    src: url(data:application/x-font-woff;charset=utf-8;base64,) format('woff');\n" +
      '    font-weight: 400;\n' +
      '    font-style: normal;\n' +
      '    -webkit-font-smoothing: antialiased;' +
      '}' +
      '</style></defs>$&'
  );
}

/*
 Splits large images using the canvas.

 This method is a fix for large images (that dont fit on one page) breaking the react-pdf/renderer
* */
function splitCanvas(
  canvas: HTMLCanvasElement,
  width: number,
  height: number,
  firstSectionHeight: number
): HTMLCanvasElement[] {
  const heightPerPage: number = A4Height300DPI - 528; // quick fix, for fixed header in pdf files
  const canvasChunks: HTMLCanvasElement[] = [];
  for (let i = 0; i * heightPerPage < height; ++i) {
    const startHeight: number = i === 0 ? 0 : firstSectionHeight + (i - 1) * heightPerPage;
    const endHeight: number = i === 0 ? firstSectionHeight : heightPerPage;
    canvasChunks.push(clipCanvasRegion(canvas, 0, startHeight, width, endHeight));
  }

  return canvasChunks;
}

/*
 Takes region [x,y] - [width,height] out of the canvas and
 transforms that region to coordinates [0,0] - [width,height]
 */
function clipCanvasRegion(
  image: CanvasImageSource,
  x: number,
  y: number,
  width: number,
  height: number
): HTMLCanvasElement {
  const canvas = document.createElement('canvas');
  const ctx = canvas.getContext('2d');

  canvas.width = width;
  canvas.height = height;

  ctx.drawImage(image, x, y, width, height, 0, 0, width, height);
  return canvas;
}

export { transformSVGStringToImage, addLatoFontToSVG };
